sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var ga=new GlideAjax('Preevent_Needed_date');
	ga.addParam('sysparm_name','what_needed');
	ga.addParam('sysparm_date',g_form.getValue('u_when_needed'));
	ga.getXML(Date);

	function Date(response){
		var answer=response.responseXML.documentElement.getAttribute('answer');
	//	alert(answer);
		if(answer=='false'){
		g_form.addErrorMessage('Please select date after the current date');
		g_form.clearValue('u_when_needed');
		}
		else{

			g_form.clearMessages();
		}
	}


   //Type appropriate comment here, and begin script below

}
