sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here

	var requestuser = request.pathParams.user_name;

	var requestINquery = request.queryParams.ni_query;

	var requestUserName = new GlideRecord('sys_user');
	requestUserName.get('user_name',requestuser);

	var recCount = new GlideAggregate('x_58872_needit_needit');
	recCount.addAggregate('COUNT');
	recCount.addQuery('u_requested_for',requestUserName.sys_id);
	recCount.addEncodedQuery(requestINquery);
	recCount.groupBy('u_request_type');
	recCount.query();


	var needitRecs = 0;
    var niUserRecs = [];
    var needitRecsCount = 0;


	while (recCount.next()) {
  needitRecs = recCount.getAggregate('COUNT');
  var reqType = recCount.u_request_type.getDisplayValue();
  niUserRecs.push({"requestType": reqType, "count": needitRecs});
  needitRecsCount = parseInt(needitRecsCount) + parseInt(needitRecs);


}


	var body = {};

	body.totalUserNIRecs =needitRecsCount;
	body.catCounts = niUserRecs;
	body.user = {"User name": requestUserName.user_name,
"User ID": requestUserName.sys_id};

	response.setBody(body);
	return new sn_ws_err.NotFoundError('The resource you requested is not part of the API.');


})(request, response);
